/**
 * Get birthdate for a player by their API ID
 */
export function getPlayerBirthdate(apiId: number): string | undefined {
  return PLAYER_BIRTHDATES[apiId];
}

/**
 * Calculate age from birthdate string (YYYY-MM-DD or ISO datetime format)
 * Returns null if birthdate is invalid or not provided
 */
export function calculateAge(birthdate: string | undefined): number | null {
  if (!birthdate) return null;
  
  try {
    // Extract just the date part if it includes timestamp (YYYY-MM-DD or YYYY-MM-DDTHH:mm:ss)
    const dateOnly = birthdate.split('T')[0] || birthdate.split(' ')[0] || birthdate;
    const birth = new Date(dateOnly);
    const today = new Date();
    
    // Check if date is valid
    if (isNaN(birth.getTime())) {
      return null;
    }
    
    let age = today.getFullYear() - birth.getFullYear();
    const monthDiff = today.getMonth() - birth.getMonth();
    
    // Adjust age if birthday hasn't occurred this year
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
      age--;
    }
    
    return age;
  } catch (error) {
    return null;
  }
}
